import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';

const Song = (props) => {

  return (
    <View style={styles.item}>
        <Text style={styles.itemText}>{props.song_name + " —— " + props.artist}</Text>
        <Text style={styles.rating}>{props.rating}</Text>
        <TouchableOpacity style={styles.square} onPress={() => props.touchSquare(props.index)}>
            <Text>X</Text>
        </TouchableOpacity>

      </View>
  )
}

const styles = StyleSheet.create({
  item: {
    backgroundColor: '#FFF',
    padding: 15,
    borderRadius: 10,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginBottom: 20,
  },
  itemLeft: {
    flexDirection: 'row',
    alignItems: 'center',
    flexWrap: 'wrap'
  },
  square: {
    justifyContent: 'center',
    alignItems: 'center',
    borderColor: '#C0C0C0',
    borderWidth: 1,
    width: 24,
    height: 24,
    backgroundColor: '#55BCF6',
    opacity: 0.4,
    borderRadius: 5,
    marginLeft: 15,
  },
  itemText: {
    maxWidth: '80%',
  },
  rating: {
    fontWeight: 'bold',
    justifyContent: 'center',
    alignItems: 'center',
    width: 30,
    height: 30,
    borderColor: '#55BCF6',
    borderWidth: 2,
    borderRadius:5,
  },
});

export default Song;
