import React, {useState} from 'react';
import { KeyboardAvoidingView, StyleSheet, Text, View, TextInput, TouchableOpacity, Keyboard, ScrollView } from 'react-native';
import Song from './Song';

export default function SongList() {
  const [song, setSong] = useState();
  const [artist, setArtist] = useState();
  const [songItems, setSongItems] = useState([]);

  const handleAddSong = () => {
    Keyboard.dismiss();
    setSongItems([...songItems, song])
    setSong(null);
  }

  const completeSong = (index) => {
    let itemsCopy = [...songItems];
    itemsCopy.splice(index, 1);
    setSongItems(itemsCopy)
  }

  return (
    <View style={styles.container}>
      {/* Added this scroll view to enable scrolling when list gets longer than the page */}
      <ScrollView
        contentContainerStyle={{
          flexGrow: 1
        }}
        keyboardShouldPersistTaps='handled'
      >

      {/* Songs */}
      <View style={styles.songsWrapper}>
        <View style={styles.items}>
          {/* This is where the songs will go! */}
          {
            songItems.map((item, index) => {
              return (
                <TouchableOpacity key={index}  onPress={() => handleAddSong()}>
                  <Song song_name={item} touchSquare={completeSong}/> 
                </TouchableOpacity>
              )
            })
          }
        </View>
      </View>

      </ScrollView>

      {/* Write a song */}
      {/* Uses a keyboard avoiding view which ensures the keyboard does not cover the items on screen */}
      <KeyboardAvoidingView 
        behavior={Platform.OS === "ios" ? "padding" : "height"}
        style={styles.writeSongWrapper}
      >
        <TextInput style={styles.input} placeholder={'Write a song name'} value={song} onChangeText={text => setSong(text)} />

        <TouchableOpacity onPress={() => handleAddSong()}>
          <View style={styles.addWrapper}>
            <Text style={styles.addText}>+</Text>
          </View>
        </TouchableOpacity>
      </KeyboardAvoidingView>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E8EAED',
  },
  songsWrapper: {
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold'
  },
  items: {
    marginTop: 30,
  },
  writeSongWrapper: {
    position: 'absolute',
    bottom: 60,
    width: '100%',
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center'
  },
  input: {
    paddingVertical: 15,
    paddingHorizontal: 15,
    backgroundColor: '#FFF',
    borderRadius: 60,
    borderColor: '#C0C0C0',
    borderWidth: 1,
    width: 250,
  },
  addWrapper: {
    width: 60,
    height: 60,
    backgroundColor: '#FFF',
    borderRadius: 60,
    justifyContent: 'center',
    alignItems: 'center',
    borderColor: '#C0C0C0',
    borderWidth: 1,
  },
  addText: {},
});
