import React, {useState, useEffect} from 'react';
import { KeyboardAvoidingView, StyleSheet, Text, View, TextInput, TouchableOpacity, Keyboard, ScrollView } from 'react-native';
import Song from './Song';
import Router from './Router'

export default function SongList({ navigation }) {
  const [song, setSong] = useState();
  const [artist, setArtist] = useState();
  const [rating, setRating] = useState();
  const [songItems, setSongItems] = useState([]);
  const [count, setCount] = useState(1)


  const handleAddSong = () => {
    Keyboard.dismiss();
    if (count == 3) {
        setSongItems([...songItems, [song, artist, rating] ])
        setSong(null);
        setArtist(null);
        setRating(null);
    }

  }

  const completeSong = (index) => {
    let itemsCopy = [...songItems];
    itemsCopy.splice(index, 1);
    setSongItems(itemsCopy)
  }

  const songDetails = () => {
      switch (count) {
        case 1 :
            return <TextInput style={styles.input} placeholder={'Write a song`s name'} value={song} onChangeText={text => setSong(text)} />;
        case 2: 
            return <TextInput style={styles.input} placeholder={'Now, this song`s artist'} value={artist} onChangeText={text => setArtist(text)} />;
        case 3: 
            return <TextInput style={styles.input} placeholder={'Now, it`s rating'} value={rating} onChangeText={text => setRating(text)} />;
      }
  }

  return (
    <View style={styles.container}>
      {/* Added this scroll view to enable scrolling when list gets longer than the page */}
      <ScrollView
        contentContainerStyle={{
          flexGrow: 1
        }}
        keyboardShouldPersistTaps='handled'
      >

      {/* Songs */}
      <View style={styles.songsWrapper}>
        <View style={styles.items}>
          {/* This is where the songs will go! */}
          {
            songItems.map((item, index) => {
              return (
                <TouchableOpacity  onPress={() => navigation.navigate("RatingForm")}>
                  <Song song_name={item[0]} artist={item[1]} rating={item[2]} index={index} touchSquare={completeSong}/> 
                </TouchableOpacity>
              )
            })
          }
        </View>
      </View>

      </ScrollView>

      {/* Write a song */}
      {/* Uses a keyboard avoiding view which ensures the keyboard does not cover the items on screen */}
      <KeyboardAvoidingView 
        behavior={Platform.OS === "ios" ? "padding" : "height"}
        style={styles.writeSongWrapper}
      >
          <View>{songDetails()}</View>

        <TouchableOpacity onPress={() => {
            console.log(count)
            if (count == 3) {
                setCount(1)
                handleAddSong()
                //add the song to the server
            }
            else{

                setCount(count + 1)
            }

        }}>
          <View style={styles.addWrapper}>
            <Text style={styles.addText}>+</Text>
          </View>
        </TouchableOpacity>
      </KeyboardAvoidingView>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E8EAED',
  },
  songsWrapper: {
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold'
  },
  items: {
    marginTop: 30,
  },
  writeSongWrapper: {
    position: 'absolute',
    bottom: 60,
    width: '100%',
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center'
  },
  input: {
    paddingVertical: 15,
    paddingHorizontal: 15,
    backgroundColor: '#FFF',
    borderRadius: 60,
    borderColor: '#C0C0C0',
    borderWidth: 1,
    width: 250,
  },
  addWrapper: {
    width: 60,
    height: 60,
    backgroundColor: '#FFF',
    borderRadius: 60,
    justifyContent: 'center',
    alignItems: 'center',
    borderColor: '#C0C0C0',
    borderWidth: 1,
  },
  addText: {},
});
